package org.tnsif.operator;

public class BitWiseOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=15, y=3;
		System.out.println(x&y);
		System.out.println(x|y);
		System.out.println(x^y);
		System.out.println(x>>y);
		System.out.println(x<<y);
		
		

	}

}
